const { Client } = require('pg');

const client = new Client({
  user: 'gerente',
  host: 'localhost',
  database: 'ventas',
  password: '123',
  port: 55432,
});

async function connectToDB() {
  try {
    await client.connect();
    const result = await client.query('SELECT * FROM empleado;');
    console.log(result.rows);
  } catch (error) {
    console.error('Error:', error);
  } finally {
    await client.end();
  }
}

connectToDB();